// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract LoanSystem {
    // Struct to store loan details
    struct Loan {
        address borrower;
        address lender;
        uint256 amount;
        uint256 collateral;
        uint256 interestRate;
        uint256 startTime;
        uint256 endTime;
        bool isActive;
    }

    // Mapping to store loans
    mapping(uint256 => Loan) public loans;
    uint256 public loanCount;

    // Event to log loan creation
    event LoanCreated(uint256 loanId, address borrower, address lender, uint256 amount, uint256 collateral, uint256 interestRate, uint256 startTime, uint256 endTime);

    // Event to log loan repayment
    event LoanRepaid(uint256 loanId, address borrower, uint256 amount);

    // Constructor
    constructor() {}

    // Function to create a new loan
    function createLoan(address borrower, uint256 amount, uint256 collateral, uint256 interestRate, uint256 duration) external payable {
        require(amount > 0, "Amount must be greater than zero");
        require(collateral >= amount, "Collateral must be at least the loan amount");
        require(interestRate > 0, "Interest rate must be greater than zero");
        require(duration > 0, "Duration must be greater than zero");

        // Transfer collateral from borrower to contract
        (bool success, ) = borrower.call{value: collateral}("");
        require(success, "Transfer of collateral failed");

        // Create loan
        loanCount++;
        loans[loanCount] = Loan({
            borrower: borrower,
            lender: msg.sender,
            amount: amount,
            collateral: collateral,
            interestRate: interestRate,
            startTime: block.timestamp,
            endTime: block.timestamp + duration,
            isActive: true
        });

        // Emit event
        emit LoanCreated(loanCount, borrower, msg.sender, amount, collateral, interestRate, block.timestamp, block.timestamp + duration);
    }

    // Function to repay a loan
    function repayLoan(uint256 loanId) external payable {
        Loan storage loan = loans[loanId];
        require(loan.isActive, "Loan is not active");
        require(msg.sender == loan.borrower, "Only the borrower can repay the loan");
        require(block.timestamp <= loan.endTime, "Loan has expired");

        uint256 totalAmount = loan.amount + (loan.amount * loan.interestRate / 100);
        require(msg.value >= totalAmount, "Insufficient funds to repay the loan");

        // Transfer collateral back to borrower
        (bool success, ) = loan.borrower.call{value: loan.collateral}("");
        require(success, "Transfer of collateral failed");

        // Transfer loan amount and interest to lender
        (success, ) = loan.lender.call{value: totalAmount}("");
        require(success, "Transfer of loan amount and interest failed");

        // Update loan status
        loan.isActive = false;

        // Emit event
        emit LoanRepaid(loanId, loan.borrower, totalAmount);
    }
}

contracts/escow.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract Escrow {
    // State variables
    address public sender;
    address public recipient;
    uint256 public releaseAmount;
    bool public isReleased;

    // Constructor to initialize the contract
    constructor(address _recipient, uint256 _releaseAmount) {
        sender = msg.sender;
        recipient = _recipient;
        releaseAmount = _releaseAmount;
        isReleased = false;
    }

    // Function to deposit ETH into the escrow
    receive() external payable {
        require(msg.value == releaseAmount, "Incorrect amount deposited");
    }

    // Function to release the ETH to the recipient
    function release() external {
        require(msg.sender == sender, "Only the sender can release the funds");
        require(!isReleased, "Funds have already been released");

        (bool success, ) = recipient.call{value: releaseAmount}("");
        require(success, "Transfer failed");

        isReleased = true;
    }

    // Function to withdraw the ETH if the condition is not met
    function withdraw() external {
        require(msg.sender == sender, "Only the sender can withdraw the funds");
        require(!isReleased, "Funds have already been released");

        (bool success, ) = sender.call{value: address(this).balance}("");
        require(success, "Transfer failed");

        isReleased = true;
    }
}

contracts/insurance.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract CorporateBond {
    // State variables
    string public name = "Corporate Bond";
    string public symbol = "CBOND";
    uint8 public decimals = 18;
    uint256 public totalSupply;
    mapping(address => uint256) public balanceOf;
    mapping(address => mapping(address => uint256)) public allowance;
    address public issuer;
    address public interestPayer;
    uint256 public interestRate = 5; // 5% interest rate
    uint256 public maturityDate;
    uint256 public interestPeriod = 7;
    mapping(address => bool) public whitelist;

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
    event InterestPaid(address indexed payer, uint256 amount);

    // Constructor
    constructor(uint256 _totalSupply, address _issuer, address _interestPayer, uint256 _maturityDate) {
        totalSupply = _totalSupply * 10 ** uint256(decimals);
        balanceOf[_issuer] = totalSupply;
        issuer = _issuer;
        interestPayer = _interestPayer;
        maturityDate = _maturityDate;
    }

    // Transfer function
    function transfer(address _to, uint256 _value) public returns (bool success) {
        require(whitelist[msg.sender], "Not whitelisted");
        require(balanceOf[msg.sender] >= _value, "Insufficient balance");
        balanceOf[msg.sender] -= _value;
        balanceOf[_to] += _value;
        emit Transfer(msg.sender, _to, _value);
        return true;
    }

    // Approve function
    function approve(address _spender, uint256 _value) public returns (bool success) {
        allowance[msg.sender][_spender] = _value;
        emit Approval(msg.sender, _spender, _value);
        return true;
    }

    // TransferFrom function
    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success) {
        require(whitelist[msg.sender], "Not whitelisted");
        require(balanceOf[_from] >= _value, "Insufficient balance");
        require(allowance[_from][msg.sender] >= _value, "Allowance exceeded");
        balanceOf[_from] -= _value;
        balanceOf[_to] += _value;
        allowance[_from][msg.sender] -= _value;
        emit Transfer(_from, _to, _value);
        return true;
    }

    // Add to whitelist
    function addToWhitelist(address _address) public {
        require(msg.sender == issuer, "Only issuer can add to whitelist");
        whitelist[_address] = true;
    }

    // Remove from whitelist
    function removeFromWhitelist(address _address) public {
        require(msg.sender == issuer, "Only issuer can remove from whitelist");
        whitelist[_address] = false;
    }

    // Pay interest
    function payInterest() public {
        require(msg.sender == interestPayer, "Only interest payer can pay interest");
        uint256 interestAmount = (balanceOf[issuer] * interestRate) / 100;
        balanceOf[issuer] -= interestAmount;
        balanceOf[msg.sender] += interestAmount;
        emit InterestPaid(msg.sender, interestAmount);
    }

    // Check if bond has matured
    function isMatured() public view returns (bool) {
        return block.timestamp >= maturityDate;
    }
}

contracts/Lock.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

// Uncomment this line to use console.log
// import "hardhat/console.sol";

contract Lock {
    uint public unlockTime;
    address payable public owner;

    event Withdrawal(uint amount, uint when);

    constructor(uint _unlockTime) payable {
        require(
            block.timestamp < _unlockTime,
            "Unlock time should be in the future"
        );

        unlockTime = _unlockTime;
        owner = payable(msg.sender);
    }

    function withdraw() public {
        // Uncomment this line, and the import of "hardhat/console.sol", to print a log in your terminal
        // console.log("Unlock time is %o and block timestamp is %o", unlockTime, block.timestamp);

        require(block.timestamp >= unlockTime, "You can't withdraw yet");
        require(msg.sender == owner, "You aren't the owner");

        emit Withdrawal(address(this).balance, block.timestamp);

        owner.transfer(address(this).balance);
    }
}

